mapper.select:
SELECT COUNT(*) 
FROM cia_ceo_info
WHERE ceo_id = #{ceoId}

SELECT * FROM cia_ceo_info
WHERE ceo_id = #{ceoId}
AND ceo_pw = #{ceoPw}

SELECT ceo_Pw
FROM cia_ceo_info
WHERE ceo_id = #{ceoId}

mapper.update:
UPDATE cia_ceo_info
SET ceo_email = #{ceoEmail}, ceo_name = #{ceoName}, ceo_pw = #{ceoPw},
	ceo_phone = #{ceoPhone}
WHERE ceo_id = #{ceoId}

UPDATE cia_shop
SET shop_category = #{shopCategory},
	shop_num = #{shopNum}, shop_info = #{shopInfo},
	shop_addr1 = #{shopAddr1}, shop_addr2 = #{shopAddr2}
WHERE shop_name = #{shopName}
